mapper.select:
SELECT SUM(revenue_rmb) as totalRevenue, SUM(order_gross_profit) as totalGrossProfit, SUM(order_net_profit) as totalNetProfit, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_gross_profit) / SUM(revenue_rmb) END as grossProfitRate, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(revenue_rmb) END as netProfitRate, CASE WHEN SUM(product_cost + first_mile_shipping) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(product_cost + first_mile_shipping) END as avgRoi FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}

SELECT category, SUM(revenue_rmb) as revenue, SUM(refund) as refundAmount, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(refund) / SUM(revenue_rmb) END as refundRate FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY category ORDER BY revenue DESC

SELECT tongtu_sku as tongtuSku, product_name as productName, SUM(shipping_quantity) as shippingQuantity, SUM(product_cost) as productCost, SUM(first_mile_shipping) as firstMileShipping, SUM(order_net_profit) as orderNetProfit, CASE WHEN SUM(product_cost + first_mile_shipping) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(product_cost + first_mile_shipping) END as roi, AVG(days_to_positive_cash_flow) as daysToPositiveCashFlow FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY tongtu_sku, product_name HAVING SUM(shipping_quantity) > 0 ORDER BY orderNetProfit DESC

SELECT first_level_category as firstLevelCategory, SUM(revenue_rmb) as revenue, SUM(order_gross_profit) as orderGrossProfit, SUM(order_net_profit) as orderNetProfit, CASE WHEN SUM(product_cost + first_mile_shipping) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(product_cost + first_mile_shipping) END as roi, AVG(days_to_positive_cash_flow) as daysToPositiveCashFlow FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY first_level_category ORDER BY revenue DESC

SELECT channel, SUM(revenue_rmb) as revenue, SUM(order_gross_profit) as orderGrossProfit, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_gross_profit) / SUM(revenue_rmb) END as grossProfitRate, SUM(order_net_profit) as orderNetProfit, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(revenue_rmb) END as netProfitRate, CASE WHEN SUM(product_cost + first_mile_shipping) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(product_cost + first_mile_shipping) END as roi, AVG(days_to_positive_cash_flow) as daysToPositiveCashFlow FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY channel ORDER BY revenue DESC

SELECT brand, SUM(revenue_rmb) as revenue, SUM(order_gross_profit) as orderGrossProfit, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_gross_profit) / SUM(revenue_rmb) END as grossProfitRate, SUM(order_net_profit) as orderNetProfit, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(revenue_rmb) END as netProfitRate, CASE WHEN SUM(product_cost + first_mile_shipping) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(product_cost + first_mile_shipping) END as roi, AVG(days_to_positive_cash_flow) as daysToPositiveCashFlow FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY brand HAVING brand IS NOT NULL AND brand != '' ORDER BY revenue DESC

SELECT country, SUM(revenue_rmb) as revenue, SUM(order_gross_profit) as orderGrossProfit, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_gross_profit) / SUM(revenue_rmb) END as grossProfitRate, SUM(order_net_profit) as orderNetProfit, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(revenue_rmb) END as netProfitRate, CASE WHEN SUM(product_cost + first_mile_shipping) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(product_cost + first_mile_shipping) END as roi, AVG(days_to_positive_cash_flow) as daysToPositiveCashFlow FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY country HAVING country IS NOT NULL AND country != '' ORDER BY revenue DESC

SELECT store_code as storeCode, channel_account as channelAccount, SUM(revenue_rmb) as revenue, SUM(order_gross_profit) as orderGrossProfit, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_gross_profit) / SUM(revenue_rmb) END as grossProfitRate, SUM(order_net_profit) as orderNetProfit, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(revenue_rmb) END as netProfitRate, CASE WHEN SUM(product_cost + first_mile_shipping) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(product_cost + first_mile_shipping) END as roi, AVG(days_to_positive_cash_flow) as daysToPositiveCashFlow FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY store_code, channel_account HAVING store_code IS NOT NULL AND store_code != '' ORDER BY revenue DESC

SELECT country, SUM(revenue_rmb) as revenue, SUM(order_net_profit) as orderNetProfit, CASE WHEN SUM(product_cost + first_mile_shipping) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(product_cost + first_mile_shipping) END as roi, AVG(days_to_positive_cash_flow) as daysToPositiveCashFlow FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY country HAVING country IS NOT NULL AND country != '' ORDER BY revenue DESC

SELECT
channel,
SUM(revenue_rmb) as revenue,
ROUND(SUM(revenue_rmb) / (SELECT SUM(revenue_rmb) FROM channel_sales_data


        AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')


        AND category = #{category}


        AND first_level_category = #{firstLevelCategory}


        AND channel = #{channel}


        AND country = #{country}


) * 100, 2) as percentage
FROM channel_sales_data


        AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')


        AND category = #{category}


        AND first_level_category = #{firstLevelCategory}


        AND channel = #{channel}


        AND country = #{country}


GROUP BY channel
ORDER BY revenue DESC

(SELECT '低ROI' as alertType, tongtu_sku as item, roi as value FROM ( SELECT tongtu_sku, CASE WHEN SUM(product_cost + first_mile_shipping) = 0 THEN 0 ELSE SUM(order_net_profit) / SUM(product_cost + first_mile_shipping) END as roi FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY tongtu_sku HAVING SUM(shipping_quantity) > 10 ) t WHERE roi < 0 ORDER BY roi ASC LIMIT 5)
UNION ALL
(SELECT '高退款率' as alertType, category as item, CASE WHEN SUM(revenue_rmb) = 0 THEN 0 ELSE SUM(refund) / SUM(revenue_rmb) END as value FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
GROUP BY category HAVING SUM(revenue_rmb) > 1000 AND SUM(refund) / SUM(revenue_rmb) > 0.1 ORDER BY value DESC LIMIT 5)

SELECT
DATE(payment_settlement_date) as date,
SUM(revenue_rmb) as revenue,
SUM(order_net_profit) as profit
FROM channel_sales_data


        AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')


        AND category = #{category}


        AND first_level_category = #{firstLevelCategory}


        AND channel = #{channel}


        AND country = #{country}


GROUP BY DATE(payment_settlement_date)
ORDER BY date

SELECT tongtu_sku, product_name, channel, revenue_rmb as revenue, product_cost, first_mile_shipping, order_net_profit, roi, days_to_positive_cash_flow, CASE WHEN revenue_rmb = 0 THEN 0 ELSE refund / revenue_rmb END as refundRate FROM channel_sales_data
AND payment_settlement_date LIKE CONCAT(#{paymentSettlementDate}, '%')
AND category = #{category}
AND first_level_category = #{firstLevelCategory}
AND channel = #{channel}
AND country = #{country}
ORDER BY revenue_rmb DESC LIMIT 100
